input = [list(elem) for elem in input]
n = 1000000
graph = []
i = 0
while i < len(input[0]):
    index = 0
    while index < len(input) and input[index][i] == ".":
        index += 1
    if index == len(input):
        for j in range(len(input)):
            input[j][i] = n
    i += 1

for line in input:
    index = 0
    graph.append(line)
    while index < len(input[0]) and line[index] != "#":
        index += 1
    if index == len(input[0]):
        graph.append([n-1])

coords = []
x, y = 0, 0
for line in graph:
    y = 0
    print(line)
    if len(line) == 1:
        x += line[0]
    else:
        for elem in line:
            if elem == "#":
                coords.append((x, y))
                y += 1
            elif elem == ".":
                y += 1
            elif type(elem) is int:
                y += elem
        x += 1

print(coords)
print(len(coords))
total = 0
while len(coords) > 0:
    pos = coords.pop(0)
    for elem in coords:
        total += abs(pos[0]-elem[0])+abs(pos[1]-elem[1])

print(total)